mapper.update:
UPDATE BOARD_T
   SET HIT = HIT + 1
 WHERE BOARD_NO = #{boardNo}

mapper.insert:

 SELECT BOARD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BOARD_T (
     BOARD_NO
   , TITLE
   , CONTENTS
   , CREATE_DT
   , MODIFY_DT
   , USER_NO  
 ) VALUES (
     #{boardNo}
   , #{title}
   , #{contents}
   , SYSTIMESTAMP
   , SYSTIMESTAMP 
   , #{user.userNo}
 )

INSERT INTO ATTACH_T (
    ATTACH_NO
  , UPLOAD_PATH
  , FILESYSTEM_NAME
  , ORIGINAL_FILENAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , BOARD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{uploadPath}
  , #{filesystemName}
  , #{originalFilename}
  , 0    
  , #{hasThumbnail}
  , #{boardNo}
)

mapper.select:
SELECT COUNT(*)
  FROM COMMENT_T
 WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE COMMENT_T
   SET STATE = 0
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_NO, USER_NO, TITLE, CONTENTS, HIT, CREATE_DT, MODIFY_DT, EMAIL FROM ( SELECT ROW_NUMBER() OVER(ORDER BY B.BOARD_NO DESC, U.USER_NO ASC) AS RN, B.BOARD_NO, B.TITLE, B.CONTENTS, B.CREATE_DT, B.MODIFY_DT, B.HIT, U.USER_NO, U.EMAIL FROM USER_T U INNER JOIN BOARD_T B ON U.USER_NO = B.USER_NO WHERE
B.TITLE LIKE '%' || #{query} || '%'
U.EMAIL LIKE '%' || #{query} || '%'
B.CONTENTS LIKE '%' || #{query} || '%'
1=1
) WHERE RN BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE BOARD_T
   SET TITLE = #{title}
     , CONTENTS = #{contents}
     , MODIFY_DT = SYSTIMESTAMP
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME, DOWNLOAD_COUNT, HAS_THUMBNAIL
  FROM ATTACH_T
 WHERE BOARD_NO = #{boardNo}

SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME, DOWNLOAD_COUNT, HAS_THUMBNAIL
  FROM ATTACH_T
