mapper.select:
SELECT USER_NO, ROLE, PW, EMAIL, NAME, GENDER, MOBILE, PW_MODIFY_DT, SIGNUP_DT
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}

mapper.insert:
INSERT INTO USER_T (
  USER_NO
, ROLE
, PW
, EMAIL
, NAME
, GENDER
, MOBILE
, PW_MODIFY_DT
, SIGNUP_DT
) VALUES (
    USER_SEQ.NEXTVAL
  , 0
  , #{pw}
  , #{email}
  , #{name}
  , #{gender}
  , #{mobile}
  , CURRENT_DATE
  , CURRENT_DATE
)
